/* for ribbon control */

.in-cart-course {
  box-shadow: 0 0 10px black;
  -webkit-transition: box-shadow linear 0.5s;
  transition: box-shadow linear 0.5s;
}

.ribbon-parent {
  position: relative;
  overflow: hidden;
}

.corner-ribbon {
  width: 120px;
  line-height: 18px;
  top: 5px;
  right: -20px;
  position: absolute !important;
  text-align: center;
  letter-spacing: 1px;
  color: #f0f0f0;
  transform: rotate(40deg);
  -webkit-transform: rotate(40deg);
}

.corner-ribbon.shadow {
  box-shadow: 0 0 3px rgba(0, 0, 0, .3);
}

.corner-ribbon.cart {
  background: #39d;
  -moz-box-shadow: 0 0 0 4px #39d, 2px 1px 4px 4px rgba(10, 10, 0, .5);
  -webkit-box-shadow: 0 0 0 4px #39d, 2px 1px 4px 4px rgba(10, 10, 0, .5);
  box-shadow: 0 0 0 4px #39d, 2px 1px 6px 4px rgba(10, 10, 0, .5);
  border-top: 1px dashed #fff;
  border-bottom: 1px dashed #fff;
}

.corner-ribbon.registered {
  background: #FF7C00;
  -moz-box-shadow: 0 0 0 4px #FF7C00, 2px 1px 4px 4px rgba(10, 10, 0, .5);
  -webkit-box-shadow: 0 0 0 4px #FF7C00, 2px 1px 4px 4px rgba(10, 10, 0, .5);
  box-shadow: 0 0 0 4px #FF7C00, 2px 1px 6px 4px rgba(10, 10, 0, .5);
  border-top: 1px dashed #fff;
  border-bottom: 1px dashed #fff;
}


