function pageprocessorCourses171(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {

  $scope.resultfound = true;
  $scope.subject = $rootScope.subject;
  $scope.selterm = $rootScope.selterm;
  $scope.instructor = $rootScope.instructor;
  $scope.instructor_method = $rootScope.instructor_method;
  $scope.loc = $rootScope.loc;
  $rootScope.back = function() {
    $rootScope.leftToRight = true;
    window.history.back();
    //window.location.href = "index.html#app/Menu82/Menu82";
  }
  var data1 = $compile($(pagedef.datatemplate))($scope);

  $("#appContent").append(data1);
  $rootScope.showlogin = false;
  $scope.fetched = false;
  $scope.filterresult = function() {
    if ($scope.selterm == "") {
      if (window.device) {
        navigator.notification.alert("Please select a term", null, 'Alert', 'Ok');
      } else {
        alert("Please select a term");
      }
      return;
    }
    $rootScope.CoursesResult = null;
    console.log("rows:");
    console.log($scope.CoursesList.ROW);
    var results = [];
    $.each($scope.CoursesList.ROW, function(index, val) {
      var found = false;
      if (($scope.selterm == val.TERM_CODE) && ($scope.subject == "" || $scope.subject == val.SUBJECT_CODE) && ($scope.loc == "" || $scope.loc == val.CAMPUS_CODE) && ($scope.instructor == "all" || $scope.instructor == val.INSTRUCTOR || $scope.instructor == "") && ($scope.instructor_method == "all" || $scope.instructor_method == val.INSTRUCTION_METHOD || $scope.instructor_method == "")) {
        found = true;
      }

      if (found) {

        results.push(val);

      }
    });

    results.sort(function(a, b) {
      if (a.CRN < b.CRN) {
        return -1;
      } else if (a.CRN > b.CRN) {
        return 1;
      }

    });
    $rootScope.CoursesResult = results;
    if (results.length > 0) {
      $rootScope.subject = $scope.subject;
      $rootScope.selterm = $scope.selterm;
      $rootScope.instructor = $scope.instructor;
      $rootScope.instructor_method = $scope.instructor_method;
      $rootScope.loc = $scope.loc;
      $scope.resultfound = true;
      $location.path("/app/Courses171/Courses171Page3");
    } else {
      if (window.device) {
        navigator.notification.alert("No results found");
      } else {
        alert("No results found");
      }
      $scope.resultfound = false;
    }
  };
  $scope.search = function() {
    try {

      if ($rootScope.crsefetched == null || $rootScope.crsefetched == false) {
        $.blockUI();
        var url = pagedef.url + "?M=" + Math.random();

        if (window.device) {} else {
          url = "/websimulator/xml?url=" + url;
        }
        var xhr = $.ajax({
          url: url,
          dataType: "xml",
          async: true,
          data: {},
          success: function(resp) {

            try {
              var results = $.xml2json(resp);

              var temptrm = "";
              var subjcd = [];
              var loccd = [];
              var instr = [];
              var instMethod = [];
              var terms = [];
              var subjects = [];
              var locations = [];
              var instructors = [];
              var instructionMethods = [];

              $.each(results.ROW, function(i, v) {
                if (temptrm.indexOf(v.TERM_CODE) == -1) {
                  terms.push({
                    "TERM_CODE": v.TERM_CODE,
                    "TERM_NAME": v.TERM_NAME
                  });
                  temptrm += v.TERM_CODE;
                }
                /*if (subjcd.indexOf(v.COURSE_ID) == -1) {
                 subjects.push({
                 "SUBJECT_CODE": v.COURSE_ID,
                 "SUBJECT_NAME": v.SUBJECT_NAME
                 });
                 subjcd.push(v.COURSE_ID);
                 }*/

                var subfound = false;
                $.each(subjects, function(j, s) {
                  if (s.SUBJECT_CODE == v.SUBJECT_CODE) {
                    subfound = true;
                    return false;
                  }
                });
                if (!subfound) {
                  subjects.push({
                    "SUBJECT_CODE": v.SUBJECT_CODE,
                    "SUBJECT_NAME": v.SUBJECT_NAME
                  });
                }


                if (loccd.indexOf(v.CAMPUS_CODE) == -1) {
                  locations.push({
                    "LOCATION_CODE": v.CAMPUS_CODE,
                    "LOCATION_NAME": v.CAMPUS
                  });
                  loccd.push(v.CAMPUS_CODE);
                }
                if (instr.indexOf(v.INSTRUCTOR) == -1) {
                  instructors.push({
                    "INSTRUCTOR_NAME": v.INSTRUCTOR,
                    "INSTRUCTOR_NAME": v.INSTRUCTOR
                  });
                  instr.push(v.INSTRUCTOR);
                }

                if (instMethod.indexOf(v.INSTRUCTION_METHOD) == -1) {
                  instructionMethods.push({
                    "INSTRUCTION_METHOD": v.INSTRUCTION_METHOD,
                    "INSTRUCTION_METHOD": v.INSTRUCTION_METHOD
                  });
                  instMethod.push(v.INSTRUCTION_METHOD);
                }

              });


              $rootScope.terms = terms;
              /*if (terms.length == 1) {
               $scope.selterm = terms[0].TERM_CODE;
               }
               if (subjects.length == 1) {
               $scope.subject = subjects[0].SUBJECT_CODE;
               }*/
              $rootScope.subjects = subjects;

              //$scope.selterm = terms[0].TERM_CODE;


              //$scope.subject = subjects[0].SUBJECT_CODE;


              $rootScope.locations = locations;
              $rootScope.instructors = instructors;
              $rootScope.instructionMethods = instructionMethods;
              $rootScope.crsefetched = true;
              $rootScope.CoursesList = results;
              $scope.selterm = "";
              $scope.subject = "";
              $scope.instructor_method = "";
              $scope.instructor = "";
              $scope.loc = "";


              //$scope.filterresult();
              $scope.$digest();
              $.unblockUI();
            } catch (E) {

              if (window.device) {
                //navigator.notification.alert("Exception : " + E);
              } else {
                //alert("Exception : " + E);
              }
            }
          },
          error: function(e) {
            //alert("Error : " + e);
            $.unblockUI();
            apprise("Error occured. Please try again later.", {
              "verify": false,
              "textYes": "Ok"
            }, function(r) {
              $window.history.back();
            });
          }
        });
      } else {
        //$scope.filterresult();
      }

    } catch (excep) {
      //alert ("Exception: " + excep); 
    }
  }
  $scope.search();

}

