function pageprocessorfacultyweekataglance(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  $scope.facultyschedule = [];
  $scope.currentDate = "";
  $scope.selWeekDay = 0;
  $scope.classesExistForDay = false;
  $scope.inputDate = "";


  var uMonths = ['JAN', 'FEB', 'MAR', 'APR', 'MAY', 'JUN', 'JUL', 'AUG', 'SEP', 'OCT', 'NOV', 'DEC'];
  var days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
  $scope.weekdata = [{
    style: "",
    label: "M",
    displayLabel: "MON",
    dateNum: "",
    data: []
  }, {
    style: "",
    label: "T",
    displayLabel: "TUE",
    dateNum: "",
    data: []
  }, {
    style: "",
    label: "W",
    displayLabel: "WED",
    dateNum: "",
    data: []
  }, {
    style: "",
    label: "R",
    displayLabel: "THU",
    dateNum: "",
    data: []
  }, {
    style: "",
    label: "F",
    displayLabel: "FRI",
    dateNum: "",
    data: []
  }, {
    style: "",
    label: "S",
    displayLabel: "SAT",
    dateNum: "",
    data: []
  }, {
    style: "",
    label: "U",
    displayLabel: "SUN",
    dateNum: "",
    data: []
  }];

  $scope.isValidDate = function(dateString) {
    // First check for the pattern
    if (!/^\d{1,2}\/\d{1,2}\/\d{4}$/.test(dateString))
      return false;

    // Parse the date parts to integers
    var parts = dateString.split("/");
    var day = parseInt(parts[1], 10);
    var month = parseInt(parts[0], 10);
    var year = parseInt(parts[2], 10);

    // Check the ranges of month and year
    if (year < 1000 || year > 3000 || month == 0 || month > 12)
      return false;

    var monthLength = [31, 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31];

    // Adjust for leap years
    if (year % 400 == 0 || (year % 100 != 0 && year % 4 == 0))
      monthLength[1] = 29;

    // Check the range of the day
    return day > 0 && day <= monthLength[month - 1];
  };

  $scope.getSelectedDate = function(day) {
    //curr_week_start_date will be a Monday
    var currDate = new Date($scope.facultyschedule.header_info.curr_week_start_date);
    console.log("sel day is=" + day);
    console.log(currDate);
    currDate.setDate(currDate.getDate() + day);
    console.log("Select day Date is:");
    console.log(currDate);
    $scope.selectedDate = currDate;
    $scope.selectedDateString = currDate.toDateString();
    var formattedDate = "";
    if (currDate.getDate().toString().length == 1) {
      formattedDate = "0" + currDate.getDate().toString() + "-" + uMonths[currDate.getMonth()] + "-" + currDate.getFullYear();
    } else {
      formattedDate = currDate.getDate().toString() + "-" + uMonths[currDate.getMonth()] + "-" + currDate.getFullYear();
    }
    $scope.selectedDateFormatted = formattedDate;

  }

  $scope.selectDay = function(day) {
    console.log("in selectDay function");
    for (var i = 0; i < 7; i++) {
      $scope.weekdata[i].style = "";
    }
    $scope.selWeekDay = day;
    $scope.weekdata[day].style = "sel-cell-theme group-heading";
    var vfound = false;
    angular.forEach($scope.facultyschedule.class_list, function(value, key) {
      if (value.MON_DAY == $scope.weekdata[day].label ||
        value.TUE_DAY == $scope.weekdata[day].label ||
        value.WED_DAY == $scope.weekdata[day].label ||
        value.THU_DAY == $scope.weekdata[day].label ||
        value.FRI_DAY == $scope.weekdata[day].label ||
        value.SAT_DAY == $scope.weekdata[day].label ||
        value.SUN_DAY == $scope.weekdata[day].label) {
        vfound = true;
      }
    });
    $scope.classesExistForDay = vfound;
    console.log("classes found=" + vfound);
    // Get the actual date for the selected day
    console.log("before calling getSelectedDate");
    $scope.getSelectedDate(day);
  }

  $scope.setDateNumbers = function() {
    var currDate = new Date($scope.facultyschedule.header_info.curr_week_start_date);
    console.log(currDate);
    $scope.weekdata[0].dateNum = currDate.getDate();
    for (var i = 1; i < 7; i++) {
      currDate.setDate(currDate.getDate() + 1);
      $scope.weekdata[i].dateNum = currDate.getDate();
    }
    console.log("printing weekdata");
    console.log($scope.weekdata);
  }

  $scope.loadClasses = function(pDate) {
    var endpoint = "/bannerfacultyweekataglance";
    var postdata = "p_start_date=" + pDate;


    $rootScope.postAPI( endpoint, postdata, function(data) {
      console.log(data);
      if (data.p_out[0] && data.p_out[0].summary) {
        //if (window.device) {
        //  var result = JSON.parse(data.p_out[0].summary);
        //  data.p_out[0].summary = result;
        // }
        $scope.facultyschedule = data.p_out[0].summary[0];
        var currDate = new Date($scope.facultyschedule.header_info.curr_week_start_date);
        if (currDate) {
          $scope.currentDate = currDate.toDateString();
        } else {
          var newdate = new Date();
          $scope.currentDate = new Date(newdate.setDate(newdate.getDate() - newdate.getDay() + 1)).toDateString();
        }
        console.log("currentDate " + $scope.currentDate);



        $scope.nonSchClassesCount = $scope.facultyschedule.nonscheduled_classes.length;
        var classCount1 = 0;
        angular.forEach($scope.facultyschedule.class_list, function(value, key) {
          if (value.MON_DAY) {
            classCount1++;
          }
          if (value.TUE_DAY) {
            classCount1++;
          }
          if (value.WED_DAY) {
            classCount1++;
          }
          if (value.THU_DAY) {
            classCount1++;
          }
          if (value.FRI_DAY) {
            classCount1++;
          }
          if (value.SAT_DAY) {
            classCount1++;
          }
          if (value.SUN_DAY) {
            classCount1++;
          }
        });
        $scope.schClassesCount = classCount1;
        angular.forEach($scope.facultyschedule.nonscheduled_classes, function(value, key) {
          value.MINUTES = 0;
          value.displayMinutes = false;
        });
      }
      var newDate = new Date();
      var curWeekDay = days[newDate.getDay()];
      if (curWeekDay == "Sunday") {
        $scope.currentWeekDay = 6;
      } else if (curWeekDay == "Monday") {
        $scope.currentWeekDay = 0;
      } else if (curWeekDay == "Tuesday") {
        $scope.currentWeekDay = 1;
      } else if (curWeekDay == "Wednesday") {
        $scope.currentWeekDay = 2;
      } else if (curWeekDay == "Thursday") {
        $scope.currentWeekDay = 3;
      } else if (curWeekDay == "Friday") {
        $scope.currentWeekDay = 4;
      } else if (curWeekDay == "Saturday") {
        $scope.currentWeekDay = 5;
      }
      console.log($scope.currentWeekDay);
      $scope.selectDay($scope.currentWeekDay);
      $scope.setDateNumbers();
    });
  }


  $scope.loadClasses('');

  $scope.loadPreviousSchedule = function() {
    var vPrevDate = $scope.facultyschedule.header_info.prev_week_start_date;
    $scope.loadClasses(vPrevDate);

  }
  $scope.loadNextSchedule = function() {
    var vNextDate = $scope.facultyschedule.header_info.next_week_start_date;
    $scope.loadClasses(vNextDate);

  }

  $scope.showClassRoster = function(myclass) {
    $rootScope.selterm = myclass.TERM_CODE;
    $rootScope.selcrn = myclass.CRN;
    $location.path("/app/facultyweekataglance/facultyweekataglancePage2");

  }

  $scope.showUnscheduledClassRoster = function(myclass) {
    $rootScope.selterm = myclass.TERM_CODE;
    $rootScope.selcrn = myclass.CRN;
    $location.path("/app/facultyweekataglance/facultyweekataglancePage2");
  }

  $scope.showScheduleForWeek = function() {
    if (!$scope.inputDate) {
      if (window.device) {
        navigator.notification.alert("Please enter valid date in GoTo field and retry.", null, "Faculty Week at a glance", null);
      } else {
        apprise("Please enter valid date in GoTo field and retry.", {
          'verify': false,
          'textYes': "Ok"
        }, function(r) {

        });
      }
      return;
    }

    var myDate = new Date($scope.inputDate);

    var mMonth = "";
    var vMonth = myDate.getMonth() + 1;
    if (vMonth.toString().length == 1) {
      mMonth = "0" + vMonth;
    } else {
      mMonth = vMonth;
    }
    var mDay = "";
    if (myDate.getDate().toString().length == 1) {
      mDay = "0" + myDate.getDate();
    } else {
      mDay = myDate.getDate();
    }
    var formattedDate = mMonth + "/" + mDay + "/" + myDate.getFullYear();
    if (!$scope.isValidDate(formattedDate)) {
      if (window.device) {
        navigator.notification.alert("Please enter valid date in GoTo field and retry.", null, "Faculty Week at a glance", null);
      } else {
        apprise("Please enter valid date in GoTo field and retry.", {
          'verify': false,
          'textYes': "Ok"
        }, function(r) {

        });
      }
      return;
    }

    $scope.loadClasses(formattedDate);
  }


}

